INSERT INTO `Reviews` VALUES (21,'Кървава песен','Много силна и въздействаща поезия. Препоръчвам на всеки.',1,'2025-06-12 08:54:35',1,'approved'),(22,'Стон','Трогателна колекция от стихотворения, носещи лиричен дух.',2,'2025-06-12 08:54:35',1,'approved'),(23,'Вечната песен','Яворов отново доказва своето величие. Прекрасна книга.',3,'2025-06-12 08:54:35',1,'approved'),(24,'Лунни песни','Нежна и красива поезия, изпълнена с емоции.',4,'2025-06-12 08:54:35',1,'approved'),(25,'Под игото','Задължително четиво за всеки българин.',21,'2025-06-12 08:54:35',1,'approved'),(26,'Тютюн','Изключително дълбок роман, който разкрива човешките драми.',22,'2025-06-12 08:54:35',1,'approved'),(27,'Железният светилник','Историческа драма с вълнуващи персонажи.',23,'2025-06-12 08:54:35',1,'approved'),(28,'Балканският синдром','Съвременна и провокираща творба.',24,'2025-06-12 08:54:35',1,'approved'),(29,'Време разделно','Много силна историческа перспектива.',25,'2025-06-12 08:54:35',1,'approved'),(30,'Черната стрела','Интересна и морална история, подходяща за подрастващи.',26,'2025-06-12 08:54:35',1,'approved'),(31,'Последният хипар','Добре написан роман за изгубеното поколение.',27,'2025-06-12 08:54:35',1,'pending'),(32,'Нощем с белите коне','Много човешки и дълбоки разкази.',28,'2025-06-12 08:54:35',1,'pending'),(33,'Адвокатът на дявола','Интригуващ сюжет и добра динамика.',29,'2025-06-12 08:54:35',1,'pending'),(34,'Градът на мечтите','Поглед върху социализма през лична призма.',30,'2025-06-12 08:54:35',1,'pending'),(35,'Война','Трогателни текстове с голяма сила.',31,'2025-06-12 08:54:35',1,'pending'),(36,'Мостът','Прекрасна социална драма.',32,'2025-06-12 08:54:35',1,'pending'),(37,'Разкази','Йовков е майстор на детайла и човечността.',33,'2025-06-12 08:54:35',1,'pending'),(38,'Неразказаната история','Много напрегнат сюжет и добър стил.',34,'2025-06-12 08:54:35',1,'pending'),(39,'Смъртта на дядо Горио','Силен социален контекст, който напомня за Вазовата мъдрост.',35,'2025-06-12 08:54:35',1,'pending'),(40,'Черешова градина','Носталгична и романтична творба.',36,'2025-06-12 08:54:35',1,'pending'),(41,'Пролетни разкази','Не успя да ме грабне, твърде бавен ритъм.',37,'2025-06-12 08:54:35',1,'declined'),(42,'Приказка за стълбата','Твърде символична за моя вкус.',38,'2025-06-12 08:54:35',1,'declined'),(43,'Избрани разкази','Не ме впечатли, очаквах повече.',39,'2025-06-12 08:54:35',1,'declined'),(44,'Лято','Прекалено описателна и монотонна.',40,'2025-06-12 08:54:35',1,'declined'),(45,'Поеми','Тежка и тъмна поезия, не за всеки.',8,'2025-06-12 08:54:35',1,'declined'),(46,'Градушка','Прекалено метафорична, трудна за разбиране.',9,'2025-06-12 08:54:35',1,'declined'),(47,'Две души','Неубедителна любовна история.',10,'2025-06-12 08:54:35',1,'declined'),(48,'Стихотворения','Смирненски тук не е в най-добрата си форма.',11,'2025-06-12 08:54:35',1,'declined'),(49,'Септември','Неуспешен опит за политическа поезия.',12,'2025-06-12 08:54:35',1,'declined'),(50,'Две песни','Твърде кратко и не особено въздействащо.',13,'2025-06-12 08:54:35',1,'declined');
